import axios from 'axios'

class AxiosRoutes {
    constructor() {
        this.baseUrl = 'http://localhost:5000'
    }

    async postUsers(params, userInfo) {
        let valid = true

        const currentUsers = this.getUsers('/users')

        currentUsers.find(user => {
            if (user.login === userInfo.login && user.password === userInfo.password) {
                valid = false
            }
        })

        const response = valid ? axios.post(`${this.baseUrl}${params}`, userInfo)
        : null

        console.log(response)
    }

    getUsers(params) {
        const data = axios.get(`${this.baseUrl}${params}`)
            .then(response => response.data)
            .catch(error => console.log(`getUsers: ${error}`))

        console.log(data)


    }
}

export default new AxiosRoutes()
