import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    height: 25%;
    padding: 10px;

    display: flex;
    flex-direction: column;

    input {
        height: 50%;
        padding: 10px;

        border: 1px solid #222;
        border-radius: 5px;
    }

    label {
        margin-bottom: 10px;
    }

`;
